//STYLES
import styles from "./Navbar.module.scss";

//CONTEXT
import { useContext, useState } from "react";
import NavContext from "../../Context/NavContext";

//REACT ROUTER
import { NavLink, Link } from "react-router-dom";

//LOGO
import logo from "../../pics/maxmover_logo.png";
//ICONS
import {
  MdOutlineDashboardCustomize,
  MdOutlineAnalytics,
  MdOutlinedFlag,
  MdPeopleOutline,
  MdOutlineSettings,
  MdOutlineMessage,
  MdOutlineLogout,
  MdOutlineManageAccounts,
  MdOutlineKeyboardArrowRight,
} from "react-icons/md";
import { GiExpense } from "react-icons/gi";
import { BsChatLeftDots, BsTruck } from "react-icons/bs";
import { VscServerEnvironment } from "react-icons/vsc";
import { AiOutlineFileSearch } from "react-icons/ai";

const NavUrl = ({ url, icon, description }) => {


  return (
    <li className={styles.li_navlink}>
      <NavLink
        to={`${url}`}
        className={({ isActive }) => (isActive ? styles.active : undefined)}
        onClick={() => checkWindowSize()}
      >
        {icon}
        <span className={styles.description}>{description}</span>
        <span className={styles.drowpdownicon}>
          <MdOutlineKeyboardArrowRight />
        </span>
      </NavLink>
    </li>
  );
};

const Navbar = () => {
  const { nav, setNav } = useContext(NavContext);
  const checkWindowSize = () => {
    if (window.innerWidth < 1240) setNav(!nav);
  };
  let menuItems = [
    {
      title: "User Management",
      subItems: [
        { title: "My Account", link: "my_account" },
        { title: "Setting", link: "settings" },
        { title: "Lock Screen", link: "lock_screen" },
      ],
    },
    {
      title: "Inquiry Management",
      subItems: [
        { title: "My Account", link: "my_account" },
        { title: "Setting", link: "settings" },
        { title: "Lock Screen", link: "lock_screen" },
      ],
    },
  ];

  const MenuItem = ({ menu }) => {
    const [showItems, setShowItems] = useState(false);
    return (
      <div>
        <h5 onClick={() => setShowItems(!showItems)}>{menu.title}</h5>
        <div className={styles.settings}>
          {showItems &&
            menu.subItems.map((subItem) => (
              <Link to={subItem.link}>&nbsp; {subItem.title}</Link>
            ))}
        </div>
      </div>
    );
  };
  return (
    <div
      className={`${styles.navbar_container} ${
        nav ? styles.navbar_mobile_active : undefined
      }`}
    >
      <nav className={nav ? undefined : styles.nav_small}>
        {/* LOGO */}
        <div className="text-center">
          {/* <VscDashboard className={styles.logo_icon} /> */}
          <div className="logo">
            <img
              src={logo}
              width="100"
              alt=""
              onClick={() => {
                setNav(!nav);
              }}
            />
          </div>
        </div>

        {/* MENU */}
        <ul className={styles.menu_container}>
          {/* FIRST CATEGORY */}
          {menuItems.map((menu) => (
            <MenuItem menu={menu} />
          ))}
        </ul>

        {/* LOGOUT BUTTON */}
        <div
          className={`${styles.btn_logout}`}
          onClick={() => {
            setNav(!nav);
          }}
        >
          <MdOutlineLogout />
        </div>
      </nav>

      <div
        className={nav ? styles.mobile_nav_background_active : undefined}
        onClick={() => {
          setNav(!nav);
        }}
      ></div>
    </div>
  );
};

export default Navbar;
