//REACT ROUTER
import { Link } from "react-router-dom";

//HOOKS
import useClickOutside from "../../../CustomHooks/ClickOutside";
import { useState } from "react";

//ICONS , PICS , STYLES
import ProfilePic from "../../../pics/people1.jpg";
import styles from "./MyProfile.module.scss";
import { MdKeyboardArrowDown, MdOutlineSettings } from "react-icons/md";
import { ReactComponent as Avatar } from "../../../pics/avatar.svg";

const MyProfile = () => {
  const [isProfileOpen, setisProfileOpen] = useState(false);
  let domNode = useClickOutside(() => {
    setisProfileOpen(false);
  });
  return (
    <div ref={domNode} className={styles.avatar_container}>
      <div
        onClick={() => {
          setisProfileOpen(!isProfileOpen);
        }}
      >
        {/* AVATAR ICON */}
        {/* <div className={styles.icon_avatar_container}>
        <Avatar />
      </div> */}
        <div className="profileav">
          <img src={ProfilePic} width="50" />
        </div>
        {/* NAME */}
        <div className={styles.name}>
          <span>Jonas Luke</span>
          <MdKeyboardArrowDown />
        </div>
      </div>

      <div className="Setting mx-2">
        <MdOutlineSettings />
      </div>

      {/* AVATAR/SETTINGS SUBMENU */}
      <div
        className={`${styles.menu} ${isProfileOpen ? styles.menu_active : ""}`}
      >
        <div className={styles.info}>
          <span className={styles.name}>WebDesign</span>

          <span className={styles.role}>Adminstrator</span>
        </div>
        <div className={styles.settings}>
          <Link to="/">Settings</Link>
          <Link to="/">Sign Out</Link>
        </div>
      </div>
    </div>
  );
};

export default MyProfile;
