//STYLES
import styles from "./Navbar.module.scss";

//CONTEXT
import { useContext } from "react";
import NavContext from "../../Context/NavContext";

//REACT ROUTER
import { NavLink } from "react-router-dom";

//LOGO
import logo from "../../pics/maxmover_logo.png";
//ICONS
import {
  MdOutlineDashboardCustomize,
  MdOutlineAnalytics,
  MdOutlinedFlag,
  MdPeopleOutline,
  MdOutlineSettings,
  MdOutlineMessage,
  MdOutlineLogout,
  MdOutlineManageAccounts,
} from "react-icons/md";
import { GiExpense } from "react-icons/gi";
import { BsChatLeftDots, BsTruck } from "react-icons/bs";
import { VscServerEnvironment } from "react-icons/vsc";
import { AiOutlineFileSearch } from "react-icons/ai";

const NavUrl = ({ url, icon, description }) => {
  const { nav, setNav } = useContext(NavContext);
  const checkWindowSize = () => {
    if (window.innerWidth < 3000) setNav(!nav);
  };

  return (
    <li className={styles.li_navlink}>
      <NavLink
        to={`${url}`}
        className={({ isActive }) => (isActive ? styles.active : undefined)}
        onClick={() => checkWindowSize()}
      >
        {icon}
        <span className={styles.description}>{description}</span>
      </NavLink>
    </li>
  );
};

const Navbar = () => {
  const { nav, setNav } = useContext(NavContext);

  return (
    <div
      className={`${styles.navbar_container} ${
        nav ? styles.navbar_mobile_active : undefined
      }`}
    >
      <nav className={nav ? undefined : styles.nav_small}>
        {/* LOGO */}
        <div className="text-center">
          {/* <VscDashboard className={styles.logo_icon} /> */}
          <div className="logo">
            <img
              src={logo}
              width="100"
              onClick={() => {
                setNav(!nav);
              }}
            />
          </div>
        </div>

        {/* MENU */}
        <ul className={styles.menu_container}>
          {/* FIRST CATEGORY */}

          <NavUrl
            url="/"
            icon={<MdOutlineDashboardCustomize />}
            description="Dashboard"
          />
          <NavUrl
            url="usermanage"
            icon={<MdPeopleOutline />}
            description="User Management"
          />
          <NavUrl
            url="inquirymanage"
            icon={<AiOutlineFileSearch />}
            description="Inquiry Management"
          />
          <NavUrl
            url="jobpaymentmanage"
            icon={<GiExpense />}
            description="Job Payment Management"
          />

          <NavUrl
            url="accountmanage"
            icon={<MdOutlineManageAccounts />}
            description="Account Management"
          />

          <NavUrl
            url="maxcliq"
            icon={<BsChatLeftDots />}
            description="Max Cliq"
          />

          <NavUrl
            url="servicecategories"
            icon={<MdOutlineSettings />}
            description="Service Categories"
          />

          <NavUrl
            url="vehiclemanage"
            icon={<BsTruck />}
            description="Vehicle Management"
          />

          <NavUrl
            url="tempnotes"
            icon={<VscServerEnvironment />}
            description="Templates & Notes"
          />

          <NavUrl
            url="smsmanage"
            icon={<BsChatLeftDots />}
            description="SMS Management"
          />
        </ul>

        {/* LOGOUT BUTTON */}
        <div
          className={`${styles.btn_logout}`}
          onClick={() => {
            setNav(!nav);
          }}
        >
          <MdOutlineLogout />
        </div>
      </nav>

      <div
        className={nav ? styles.mobile_nav_background_active : undefined}
        onClick={() => {
          setNav(!nav);
        }}
      ></div>
    </div>
  );
};

export default Navbar;
