//REACT ROUTER
import { Link } from "react-router-dom";

//HOOKS
import useClickOutside from "../../../CustomHooks/ClickOutside";
import { useState } from "react";

//ICONS , PICS , STYLES
import ProfilePic from "../../../pics/people1.jpg";
import styles from "./MyProfile.module.scss";
import { MdKeyboardArrowDown, MdOutlineSettings } from "react-icons/md";
import { ReactComponent as Avatar } from "../../../pics/avatar.svg";
import React from "react";
import ReactDOM from "react-dom";
import Modal from "react-modal";

const customStyles = {
  content: {
    top: "50%",
    left: "50%",
    right: "auto",
    bottom: "auto",
    marginRight: "-50%",
    transform: "translate(-50%, -50%)",
  },
};

// Make sure to bind modal to your appElement (http://reactcommunity.org/react-modal/accessibility/)
Modal.setAppElement("#yourAppElement");

function App() {
  var subtitle;
  const [modalIsOpen, setIsOpen] = React.useState(false);
  function openModal() {
    setIsOpen(true);
  }

  function afterOpenModal() {
    // references are now sync'd and can be accessed.
    subtitle.style.color = "#f00";
  }

  function closeModal() {
    setIsOpen(false);
  }

  return (
    <div>
      <button onClick={openModal}>Open Modal</button>
      <Modal
        isOpen={modalIsOpen}
        onAfterOpen={afterOpenModal}
        onRequestClose={closeModal}
        style={customStyles}
        contentLabel="Example Modal"
      >
        <h2 ref={(_subtitle) => (subtitle = _subtitle)}>Hello</h2>
        <button onClick={closeModal}>close</button>
        <div>I am a modal</div>
        <form>
          <input />
          <button>tab navigation</button>
          <button>stays</button>
          <button>inside</button>
          <button>the modal</button>
        </form>
      </Modal>
    </div>
  );
}

ReactDOM.render(<App />, appElement);

const MyProfile = () => {
  const [isProfileOpen, setisProfileOpen] = useState(false);
  let domNode = useClickOutside(() => {
    setisProfileOpen(false);
  });
  return (
    <div ref={domNode} className={styles.avatar_container}>
      <div
        className="d-flex"
        onClick={() => {
          setisProfileOpen(!isProfileOpen);
        }}
      >
        {/* AVATAR ICON */}
        {/* <div className={styles.icon_avatar_container}>
        <Avatar />
      </div> */}
        <div className="profileav">
          <img src={ProfilePic} width="50" />
        </div>
        {/* NAME */}
        <div className={styles.name}>
          <span>Jonas Luke</span>
          <MdKeyboardArrowDown />
        </div>
        {/* AVATAR/SETTINGS SUBMENU */}
        <div
          className={`${styles.menu} ${
            isProfileOpen ? styles.menu_active : ""
          }`}
        >
          <div className={styles.info}>
            <span className={styles.name}>WebDesign</span>

            <span className={styles.role}>Adminstrator</span>
          </div>
          <div className={styles.settings}>
            <Link to="/">Settings</Link>
            <Link to="/">Sign Out</Link>
          </div>
        </div>
      </div>

      <div className="Setting mx-2">
        <MdOutlineSettings />
      </div>
    </div>
  );
};

export default MyProfile;
