const Search = () =>  {
  const state = {
    data: null,
    search: "",
    color: ""
  }

  const render = () => {
    let { data, search, color } = this.state;
    if (!data) {
      return <p>Loading</p>
    }
    let filtered = data.filter(item => item.name.toLowerCase().includes(search.toLowerCase()));
    return (
      <div>
        <div className="wrapper">
          <div className="search">
            <input
              id="search"
              type="search"
              value={this.state.search}
              placeholder="Search a color by name..."
              onChange={this.onInput}
              onFocus={this.onFocus}
              onBlur={this.onBlur}
              autocomplete="off"
            />
            <i class="fas fa-search"></i>
          </div>
          {search.length > 1 && filtered.length > 0 && (
            <ul className="list">
              {filtered.map(item => (
                <li onClick={() => this.onClickItem(item)}>{item.name}</li>
              ))}
            </ul>
          )}
        </div>
        {color && (
          <p className="result">
            <b>Color:</b>
            {color.name}
            <span className="box" style={{backgroundColor: color.hexString}}/>
            {color.hexString}
          </p>
        )}
      </div>
    )
  }

  };

export default Search;
