//STYLES
import styles from "./Navbar.module.scss";

//CONTEXT
import { useContext, useState } from "react";
import NavContext from "../../Context/NavContext";

//REACT ROUTER
import { NavLink, Link, useNavigate } from "react-router-dom";

//LOGO
import logo from "../../pics/maxmover_logo.png";
//ICONS
import {
  MdOutlineDashboardCustomize,
  MdOutlineAnalytics,
  MdOutlinedFlag,
  MdPeopleOutline,
  MdOutlineSettings,
  MdOutlineMessage,
  MdOutlineLogout,
  MdOutlineManageAccounts,
  MdOutlineKeyboardArrowRight,
} from "react-icons/md";
import { GiExpense } from "react-icons/gi";
import { BsChatLeftDots, BsTruck } from "react-icons/bs";
import { VscServerEnvironment } from "react-icons/vsc";
import { AiOutlineFileSearch } from "react-icons/ai";
import { IoIosArrowDown, IoIosArrowForward } from "react-icons/io";

// const NavUrl = ({ url, icon, description }) => {
//   return (
//     <li className={styles.li_navlink}>
//       <NavLink
//         to={`${url}`}
//         className={({ isActive }) => (isActive ? styles.active : undefined)}
//         onClick={() => checkWindowSize()}>
//         {icon}
//         <span className={styles.description}>{description}</span>
//         <span className={styles.drowpdownicon}>
//           <MdOutlineKeyboardArrowRight />
//         </span>
//       </NavLink>
//     </li>
//   );
// };

const Navbar = () => {
  let navigation = useNavigate();
  const { nav, setNav } = useContext(NavContext);
  const [activeTab, setActiveTab] = useState(0);
  const checkWindowSize = () => {
    if (window.innerWidth < 1240) setNav(!nav);
  };
  const [menuItems] = useState([
    {
      title: "Dashboard",
      icon: <MdOutlineDashboardCustomize />,
      link: "/",
    },

    {
      title: "User Management",
      icon: <MdOutlineManageAccounts />,
      subItems: [
        { title: "My Account", link: "my_account" },
        { title: "Setting", link: "settings" },
        { title: "Lock Screen", link: "lock_screen" },
      ],
    },
    {
      title: "Inquiry Management",
      icon: <AiOutlineFileSearch />,
      subItems: [
        { title: "My Account", link: "my_account" },
        { title: "Setting", link: "settings" },
        { title: "Lock Screen", link: "lock_screen" },
      ],
    },
    {
      title: "Job Payment Management",
      icon: <GiExpense />,
      subItems: [
        { title: "My Account", link: "my_account" },
        { title: "Setting", link: "settings" },
        { title: "Lock Screen", link: "lock_screen" },
      ],
    },
    {
      title: "Account Management",
      icon: <MdOutlineManageAccounts />,
      subItems: [
        { title: "My Account", link: "my_account" },
        { title: "Setting", link: "settings" },
        { title: "Lock Screen", link: "lock_screen" },
      ],
    },
    {
      title: "Max Cliq",
      icon: <BsChatLeftDots />,
      subItems: [
        { title: "My Account", link: "my_account" },
        { title: "Setting", link: "settings" },
        { title: "Lock Screen", link: "lock_screen" },
      ],
    },
    {
      title: "Vehicle Management",
      icon: <BsTruck />,
      subItems: [
        { title: "My Account", link: "my_account" },
        { title: "Setting", link: "settings" },
        { title: "Lock Screen", link: "lock_screen" },
      ],
    },
    {
      title: "Templates & Notes",
      icon: <VscServerEnvironment />,
      subItems: [
        { title: "My Account", link: "my_account" },
        { title: "Setting", link: "settings" },
        { title: "Lock Screen", link: "lock_screen" },
      ],
    },
    {
      title: "SMS Management",
      icon: <BsChatLeftDots />,
      subItems: [
        { title: "My Account", link: "my_account" },
        { title: "Setting", link: "settings" },
        { title: "Lock Screen", link: "lock_screen" },
      ],
    },
  ]);

  const MenuItem = ({ menu, i }) => {
    const [showItems, setShowItems] = useState(false);
    return (
      <div className={styles.li_navlink}>
        <span
          onClick={() => {
            if (menu.isActive) menu.isActive = false;
            else menu["isActive"] = true;
            if (menu?.subItems) setShowItems(!showItems);
            else navigation(menu.link);
          }}
          className={`d-flex ${styles.mainTabs} ${
            menu.isActive ? styles.active : null
          }`}>
          <div className="mx-3">{menu.icon}</div>
          <div className={styles.description}>
            {menu.title}{" "}
            {showItems ? <IoIosArrowDown /> : <IoIosArrowForward />}
          </div>
        </span>
        {menu?.subItems && (
          <div className={styles.settings}>
            {menu?.isActive &&
              menu.subItems.map((subItem) => (
                <Link
                  onClick={() => {
                    setActiveTab(i);
                  }}
                  to={subItem.link}>
                  &nbsp; {subItem.title}
                </Link>
              ))}
          </div>
        )}
      </div>
    );
  };
  return (
    <div
      className={`${styles.navbar_container} ${
        nav ? styles.navbar_mobile_active : undefined
      }`}>
      <nav className={nav ? undefined : styles.nav_small}>
        {/* LOGO */}
        <div className="text-center">
          {/* <VscDashboard className={styles.logo_icon} /> */}
          <div className={styles.logo}>
            <img
              src={logo}
              width="100"
              alt=""
              onClick={() => {
                setNav(!nav);
              }}
            />
          </div>
        </div>

        {/* MENU */}
        <ul className={styles.menu_container}>
          {/* FIRST CATEGORY */}
          {menuItems.map((menu, i) => (
            <MenuItem menu={menu} i={i} />
          ))}
        </ul>

        {/* LOGOUT BUTTON */}
        {/* <div
          className={`${styles.btn_logout}`}
          onClick={() => {
            setNav(!nav);
          }}>
          <MdOutlineLogout />
        </div> */}
      </nav>

      <div
        className={nav ? styles.mobile_nav_background_active : undefined}
        onClick={() => {
          setNav(!nav);
        }}></div>
    </div>
  );
};

export default Navbar;
